#!/bin/bash

# T6BRD - Tekken 6 Bloodline Rebellion Security Dongle Emulator, made by dazzaXx.
# ------------------------------------------------------------------------
# The 19000009.BIN security file is for some reason padded to 268,435,456 bytes, despite actual code being only roughly 28-30KB,
# so only the actual binary data is only stored in script to save space and is padded back to the original size.
#
# The script sets up all configurations needed to make this work, it also prints out what it is doing in a .log since if the script is ran from
# a service, there's no terminal output. So refer to the log if something doesnt seem right.

set -euo pipefail

USBIMG="/T6BRD/T6BRD_USB.img"
SHLOG="/T6BRD/T6BRD.log"
LIBCOMPOSITE="/etc/modules-load.d/libcomposite.conf"
BOOTCONFIG="/boot/config.txt"
DATABIN=""
DATAUSBID="DBAC326942783AC7820C2C9B1BF8450D"
BINLOCATION="/T6BRD/USBMNT/19000009.BIN"
USBIDLOCATION="/T6BRD/USBMNT/USBID.DAT"
USBMNT="/T6BRD/USBMNT/"
SERVICE="/etc/systemd/system/T6BRD.service"
timestamp=$(date)

if [ -f "$SHLOG" ]; then # Check if the log exists, if it does then delete it and print the log header.
	rm /T6BRD/T6BRD.log
	echo "TEKKEN 6 BLOODLINE REBELLION DONGLE EMULATOR LOG" > "$SHLOG"
	echo -e "-------------------------------------------------\n" >> "$SHLOG"
fi

if [ ! -f "$SERVICE" ]; then # Check if the T6BR service doesnt exist, if not, create it.
	echo -e "\n[$timestamp] Creating Service file for autobooting." >> "$SHLOG"
	echo -e "[Unit]\nDescription=T6BRD - Tekken 6 BR Dongle Emulator\n\n[Service]\nExecStart=/T6BRD/T6BRD.sh\n\n[Install]\nWantedBy=multi-user.target" > "$SERVICE"
	systemctl enable T6BRD # Enable the service and start it.
	systemctl start T6BRD
else
	echo "[$timestamp] Service already exists, skipping." >> "$SHLOG"
fi

if ! grep -q "\[all\]" "$BOOTCONFIG"; then # Check the systems boot config for the [all] tag, if it doesnt exist, append it to the end after a newline.
	echo "[$timestamp] [all] not found in boot config, adding now." >> "$SHLOG"
	echo -e "\n[all]" >> "$BOOTCONFIG"
else
	echo -e "\n[$timestamp] [all] tag was found in boot config, skipping." >> "$SHLOG"
fi

if ! grep -q "dtoverlay=dwc2,dr_mode=peripheral" "$BOOTCONFIG"; then # Similar as above, check to see if DWC2 is added with dr_mode, if not, add it under the [all] tag.
	echo "[$timestamp] dtoverlay not found in boot config, adding now." >> "$SHLOG"
	echo "dtoverlay=dwc2,dr_mode=peripheral" >> "$BOOTCONFIG"
else
	echo "[$timestamp] dtoverlay is already in boot config, skipping." >> "$SHLOG"
fi

if [ ! -f "$LIBCOMPOSITE" ]; then # Check to see if libcomposite has a .conf file in modules-load.d , if not, create the file and print the module name within itself.
	echo "[$timestamp] libcomposite module not loaded, creating .conf file now and rebooting." >> "$SHLOG"
	echo "libcomposite" > "$LIBCOMPOSITE"
else
	echo "[$timestamp] libcomposite is already present, skipping." >> "$SHLOG" 
fi

if [ ! -f "$USBIMG" ]; then # Check if the emulated USB's storage image exists, if not, create a 265MB .IMG and format it to FAT32.
	echo "[$timestamp] Tekken 6 BR USB Image DOES NOT exist, creating blank image." >> "$SHLOG"
	dd if=/dev/zero of=/T6BRD/T6BRD_USB.img bs=1M count=265
	echo "[$timestamp] Blank Image Generated." >> "$SHLOG"
	mkdosfs -F 32 /T6BRD/T6BRD_USB.img
	echo "[$timestamp] Blank Image formatted to FAT32." >> "$SHLOG"
else
	echo "[$timestamp] Tekken 6 BR USB Image already exists, skipping." >> "$SHLOG"
fi

cd /T6BRD
mkdir -p USBMNT
echo "[$timestamp] Mounting USB .img to check contents." >> "$SHLOG"
mount -o rw "$USBIMG" "USBMNT"

if [ ! -f "$BINLOCATION" ]; then # Once the .img has been mounted, scan the root of it to see if 19000009.BIN and USBID.DAT exist, if not, build both binary files on it.
	echo "[$timestamp] 19000009.BIN not found on the USB drive, building binary file and copying over now." >> "$SHLOG"
	printf "%s" "$DATABIN" | perl -pe 's/([0-9A-Fa-f]{2})/chr(hex($1))/eg' > "$BINLOCATION"
	echo "[$timestamp] Padding 19000009.BIN to 268,435,456 bytes." >> "$SHLOG"
	truncate -s 268435456 "$BINLOCATION"
else
	echo "[$timestamp] 19000009.BIN is already on the USB drive, skipping." >> "$SHLOG"
fi

if [ ! -f "$USBIDLOCATION" ]; then
	echo "[$timestamp] USBID.DAT not found on the USB drive, building binary file and copying over now." >> "$SHLOG"
	printf "%s" "$DATAUSBID" | perl -pe 's/([0-9A-Fa-f]{2})/chr(hex($1))/eg' > "$USBIDLOCATION"
else
	echo "[$timestamp] USBID.DAT is already on the USB drive, skipping." >> "$SHLOG"
fi

echo "[$timestamp] Syncing Changes to USB drive, if any." >> "$SHLOG"
sync
echo "[$timestamp] Unmounting USB .img now." >> "$SHLOG"
umount "$USBMNT" # Sync changes if any has been made and unmount the .img before it's properly mounted as a USB drive.

echo "[$timestamp] Setting up USB drive now! If there is an error past this point, or the USB is already active, the log will stop here." >> "$SHLOG"

mkdir -p /sys/kernel/config/usb_gadget/Tekken6BR_Dongle
cd /sys/kernel/config/usb_gadget/Tekken6BR_Dongle
echo 0x0693 > idVendor
echo 0x0026 > idProduct
echo 0x0100 > bcdDevice
echo 0x0200 > bcdUSB
mkdir -p strings/0x409
echo "026450800000" > strings/0x409/serialnumber
echo "dazzaXx" > strings/0x409/manufacturer
echo "Tekken 6 BR Dongle Emulator" > strings/0x409/product
mkdir -p configs/c.1/strings/0x409
echo "Config 1: Mass Storage" > configs/c.1/strings/0x409/configuration
echo 250 > configs/c.1/MaxPower
mkdir -p functions/mass_storage.usb0
echo 0 > functions/mass_storage.usb0/lun.0/cdrom
echo 0 > functions/mass_storage.usb0/lun.0/ro
echo "$USBIMG" > functions/mass_storage.usb0/lun.0/file
ln -s functions/mass_storage.usb0 configs/c.1/
ls /sys/class/udc > UDC

echo "[$timestamp] USB drive should be mounted and security files already copied to the USB drive, have a nice day!" >> "$SHLOG"
